mapper.update:
   update riunione set 
     data_comunicazione=#{data_comunicazione}
   , data_riunione=#{data_riunione}
   , inizio_riunione=#{inizio_riunione}
   , fine_riunione=#{fine_riunione}
   , ordine_del_giorno=#{ordine_del_giorno}
   , luogo=#{luogo}
   , stato=#{stato}
   , indirizzo_abitazione=#{indirizzo_abitazione}
   where
   id = #{id}
