mapper.insert:
insert into user (cf, nome, cognome, email, password, telefono, data_in, data_out, id_ruolo, id_abitazione) values (
	 #{cf}
	 , #{nome}
	 , #{cognome}
	 , #{email}
	 , #{password}
	 , #{telefono}
	 , #{data_in}
	 , #{data_out}
	 , #{id_ruolo}
	 , #{id_abitazione}
)

mapper.update:
  update user set 
    cf=#{cf}
    , nome=#{nome}
  	 , cognome=#{cognome}
  	 , email=#{email}
  	 , password=#{password}
  	 , telefono=#{telefono}
  	 , data_in=#{data_in}
  	 , data_out=#{data_out}
  	 , id_ruolo=#{id_ruolo}
  	 , id_abitazione=#{id_abitazione}
  where
  id = #{id}
